	public static int findLongest(int[] A, int n) {
        int [] c=new int[n];
        ArrayList<Queue<Integer>> arrayList=new ArrayList<Queue<Integer>>();
        for(int i=0;i<n;i++){
        	Queue<Integer> q=new LinkedList<Integer>();
        	arrayList.add(i, q);;
        }
        for(int i=0; i<n; i++){
            c[i]=1;
            for(int j=0; j<i; j++){
                if(A[j]<A[i]){
                	c[i]=Math.max(c[j]+1, c[i]);
//                	arrayList.get(i).add(A[j]);
                }
            }
        }
        int max=0;
        int index=0;
        for(int i=0; i<n; i++){
            if(c[i]>max) {
            	max=c[i];
            	index=i;
            }
        }
//        Queue<Integer> queue=arrayList.get(index);
//        while(!queue.isEmpty()){
//        	System.out.println(queue.poll()+" ");
//        }
        return max;
    }


